import { useEffect, useState } from "react";
import axios from "axios";

export default function JobList() {
  const [jobs, setJobs] = useState([]);
  const [filter, setFilter] = useState("All");

  useEffect(() => {
    axios.get("http://localhost:5000/jobs").then(res => setJobs(res.data));
  }, []);

  const filteredJobs = filter === "All" ? jobs : jobs.filter(job => job.category === filter);

  return (
    <section className="p-4">
      <h2 className="text-2xl mb-4">Job Listings</h2>
      <div className="mb-4 space-x-2">
        {["All", "Design", "Development", "Marketing"].map(cat => (
          <button key={cat} className="px-3 py-1 border rounded" onClick={() => setFilter(cat)}>
            {cat}
          </button>
        ))}
      </div>
      <div className="grid gap-4">
        {filteredJobs.map((job, index) => (
          <div key={index} className="border p-4 rounded shadow">
            <h3 className="text-xl font-bold">{job.title}</h3>
            <p>{job.company}</p>
            <p>{job.category} | {job.location}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
